// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./CourseCertificate.sol";
import "./HoneyToken.sol";

contract CourseAuction {
    CourseCertificate public courseCertificate;
    HoneyToken public honeyToken;

    struct Auction {
        uint256 courseId;
        uint256 batchId;
        uint256 startTime;
        uint256 endTime;
        uint256 minPrice;
        bool isActive;
    }

    struct Bid {
        address bidder;
        uint256 bidTime;
        uint256 amount;
    }

    // mapping：courseId => batchId => Auction
    mapping(uint256 => mapping(uint256 => Auction)) public auctions;
    // mapping：courseId => batchId => All Bids
    mapping(uint256 => mapping(uint256 => Bid[])) public bids;

    event AuctionCreated(
        uint256 courseId,
        uint256 batchId,
        uint256 startTime,
        uint256 endTime,
        uint256 minPrice
    );
    event BidPlaced(
        uint256 courseId,
        uint256 batchId,
        address bidder,
        uint256 bidTime,
        uint256 amount
    );
    event AuctionFinalized(uint256 courseId, uint256 batchId);

    modifier onlyTeacher(uint256 courseId) {
        (address teacher, , , , ) = courseCertificate.courses(courseId);
        require(
            msg.sender == teacher,
            "Only course teacher can call this function"
        );
        _;
    }

    constructor(address _courseCertificate, address _honeyToken) {
        courseCertificate = CourseCertificate(_courseCertificate);
        honeyToken = HoneyToken(_honeyToken);
    }

    function createAuction(
        uint256 courseId,
        uint256 batchId,
        uint256 startTime,
        uint256 endTime,
        uint256 minPrice
    ) external {
        // require(startTime > block.timestamp, "Start time must be in future"); // To facilitate testing, temporarily remove this restriction, but it must be enabled in the production environment.
        require(endTime > startTime, "End time must be after start time");

        auctions[courseId][batchId] = Auction({
            courseId: courseId,
            batchId: batchId,
            startTime: startTime,
            endTime: endTime,
            minPrice: minPrice,
            isActive: true
        });

        emit AuctionCreated(courseId, batchId, startTime, endTime, minPrice);
    }

    function placeBid(
        uint256 courseId,
        uint256 batchId,
        uint256 amount
    ) external {
        Auction storage auction = auctions[courseId][batchId];
        require(auction.isActive, "Auction is not active");
        // require(block.timestamp >= auction.startTime, "Auction not started"); // To facilitate testing, temporarily remove this restriction, but it must be enabled in the production environment.
        // require(block.timestamp < auction.endTime, "Auction ended"); // To facilitate testing, temporarily remove this restriction, but it must be enabled in the production environment.
        require(amount >= auction.minPrice, "Bid too low");

        require(
            honeyToken.transferFrom(msg.sender, address(this), amount),
            "Token Transfer failed"
        );

        bids[courseId][batchId].push(
            Bid({
                bidder: msg.sender,
                bidTime: block.timestamp,
                amount: amount
            })
        );

        emit BidPlaced(courseId, batchId, msg.sender, block.timestamp, amount);
    }

    // Close the bidding and confirm the winning bidder.
    function finalizeAuction(uint256 courseId, uint256 batchId)
        external
        onlyTeacher(courseId)
    {
        Auction storage auction = auctions[courseId][batchId];
        require(auction.isActive, "Auction not active");
        // require(block.timestamp >= auction.endTime, "Auction not ended"); // To facilitate testing, temporarily remove this restriction, but it must be enabled in the production environment.

        Bid[] storage auctionBids = bids[courseId][batchId];
        require(auctionBids.length > 0, "No bids placed");

        // Sort the bids.
        for (uint i = 0; i < auctionBids.length - 1; i++) {
            for (uint j = 0; j < auctionBids.length - i - 1; j++) {
                if (auctionBids[j].amount < auctionBids[j + 1].amount) {
                    Bid memory temp = auctionBids[j];
                    auctionBids[j] = auctionBids[j + 1];
                    auctionBids[j + 1] = temp;
                }
            }
        }

        // Get the maximum number of students.
        (, , , uint256 maxStudents, ) = courseCertificate.batches(
            courseId,
            batchId
        );

        // Mint NFTs for the winners.
        uint256 winnerCount = maxStudents < auctionBids.length
            ? maxStudents
            : auctionBids.length;
        for (uint256 i = 0; i < winnerCount; i++) {
            courseCertificate.mintForWinner(
                auctionBids[i].bidder,
                courseId,
                batchId
            );
        }

        // Return the bids of the non-winning bidders.
        for (uint256 i = winnerCount; i < auctionBids.length; i++) {
            require(
                honeyToken.transfer(auctionBids[i].bidder, auctionBids[i].amount),
                "Token Transfer failed"
            );
        }

        auction.isActive = false;
        emit AuctionFinalized(courseId, batchId);
    }

    function getBids(
        uint256 courseId,
        uint256 batchId
    ) public view returns (Bid[] memory) {
        return bids[courseId][batchId];
    }
}

src/CourseCertificate.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CourseCertificate is ERC1155, Ownable {
    // Course structure
    struct Course {
        address teacher;           // Teacher address
        string name;              // Course name
        string description;       // Course description
        uint256 courseId;         // Course ID
        bool isActive;            // Whether the course is active
    }

    // Course Batch structure
    struct BatchInfo {
        uint256 startTime;        // Start time
        uint256 endTime;          // End time
        uint256 maxStudents;      // Maximum number of students
        uint256 enrolledStudents; // Number of enrolled students
        bool isActive;           // Whether the batch is active
    }

    // NFT Level structure
    struct NFTLevel {
        uint256 level;           // Current level
        uint256 lastUpgradeTime; // Last upgrade time
        bool isCompleted;        // Whether the course is completed
    }

    // 映射：courseId => Course
    mapping(uint256 => Course) public courses;
    // 映射：courseId => batchId => BatchInfo
    mapping(uint256 => mapping(uint256 => BatchInfo)) public batches;
    // 映射：tokenId => NFTLevel
    mapping(uint256 => mapping(address => NFTLevel)) public nftLevels;

    // Upgrade event
    event NFTUpgraded(
        uint256 indexed tokenId,
        address indexed student,
        uint256 oldLevel,
        uint256 newLevel,
        bool isCompleted
    );

    // Course counter
    uint256 private _courseIdCounter;

    // Modifier to only allow the auction contract to call
    modifier onlyAuction() {
        require(msg.sender == auctionContract, "Only auction contract can call this");
        _;
    }

    address public auctionContract;

    constructor() ERC1155("https://your-metadata-uri/{id}.json") Ownable(msg.sender) {
        _courseIdCounter = 0;
    }

    // Set the auction contract address
    function setAuctionContract(address _auctionContract) external onlyOwner {
        auctionContract = _auctionContract;
    }

    // Create a new course
    function createCourse(
        string memory name,
        string memory description
    ) external returns (uint256) {
        uint256 courseId = _courseIdCounter++;

        courses[courseId] = Course({
            teacher: msg.sender,
            name: name,
            description: description,
            courseId: courseId,
            isActive: true
        });

        return courseId;
    }

    // Add a course batch
    function addCourseBatch(
        uint256 courseId,
        uint256 batchId,
        uint256 startTime,
        uint256 endTime,
        uint256 maxStudents
    ) external {
        require(courses[courseId].teacher == msg.sender, "Only teacher can add batch");
        require(courses[courseId].isActive, "Course is not active");
        require(startTime < endTime, "Invalid time range");

        batches[courseId][batchId] = BatchInfo({
            startTime: startTime,
            endTime: endTime,
            maxStudents: maxStudents,
            enrolledStudents: 0,
            isActive: true
        });
    }

    // Mint NFTs for the winners (only the auction contract can call)
    function mintForWinner(
        address winner,
        uint256 courseId,
        uint256 batchId
    ) external onlyAuction {
        require(courses[courseId].isActive, "Course is not active");
        require(batches[courseId][batchId].isActive, "Batch is not active");
        require(batches[courseId][batchId].enrolledStudents < batches[courseId][batchId].maxStudents, "Batch is full");

        _mint(winner, courseId, 1, "");
        batches[courseId][batchId].enrolledStudents++;
    }

    // Upgrade NFT
    function upgradeNFT(
        uint256 courseId,
        address student,
        uint256 newLevel
    ) external {
        // Check if the caller is the course teacher
        require(courses[courseId].teacher == msg.sender, "Only teacher can upgrade NFT");

        // Check if the student owns the NFT
        require(balanceOf(student, courseId) > 0, "Student does not own this NFT");

        // Get the current NFT level information
        NFTLevel storage nftLevel = nftLevels[courseId][student];

        // Ensure the new level is higher than the current level
        require(newLevel > nftLevel.level, "New level must be higher than current level");

        // If marking as completed, ensure it hasn't been completed before
        require(!nftLevel.isCompleted, "Course already completed");

        // Record the old level for the event
        uint256 oldLevel = nftLevel.level;

        // Update the NFT level information
        nftLevel.level = newLevel;
        nftLevel.lastUpgradeTime = block.timestamp;
        nftLevel.isCompleted = true;
        // Trigger the upgrade event
        emit NFTUpgraded(
            courseId,
            student,
            oldLevel,
            newLevel,
            nftLevel.isCompleted
        );
    }

    function getNFTLevel(
        uint256 courseId,
        address student
    ) external view returns (uint256 level, uint256 lastUpgradeTime, bool isCompleted) {
        NFTLevel memory nftLevel = nftLevels[courseId][student];
        return (nftLevel.level, nftLevel.lastUpgradeTime, nftLevel.isCompleted);
    }

    // Override the _update function to implement transfer restrictions
    function _update(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory values
    ) internal virtual override {
        // If minting (from is zero address), no need to check
        if (from != address(0)) {
            // Check each token to be transferred
            for (uint256 i = 0; i < ids.length; i++) {
                // If transferring (not destroying), check if transfer is allowed
                if (to != address(0)) {
                    uint256 tokenId = ids[i];
                    NFTLevel memory nftLevel = nftLevels[tokenId][from];
                    // If the NFT is completed, transfer is not allowed
                    require(!nftLevel.isCompleted, "Completed course NFT cannot be transferred");
                }
            }
        }

        // Call the implementation of the parent contract
        super._update(from, to, ids, values);
    }
}

